using Leetcode;

namespace TestLeetCode;

public class TestSolution1404
{
    [Fact]
    public void Testcase1()
    {
        const string binary = "1101";

        var numSteps = Solution1404.NumSteps(binary);

        Assert.Equal(6, numSteps);
    }

    [Fact]
    public void Testcase2()
    {
        const string binary = "10";

        var numSteps = Solution1404.NumSteps(binary);

        Assert.Equal(1, numSteps);
    }

    [Fact]
    public void Testcase3()
    {
        const string binary = "1";

        var numSteps = Solution1404.NumSteps(binary);

        Assert.Equal(0, numSteps);
    }

    [Fact]
    public void Testcase4()
    {
        const string binary = "1111011110000011100000110001011011110010111001010111110001";

        var numSteps = Solution1404.NumSteps(binary);

        Assert.Equal(85, numSteps);
    }

    [Fact]
    public void Testcase5()
    {
        const string binary = "1111110011101010110011100100101110010100101110111010111110110010";

        var numSteps = Solution1404.NumSteps(binary);

        Assert.Equal(89, numSteps);
    }

    [Fact]
    public void Testcase6()
    {
        const string binary =
            "";

        var numSteps = Solution1404.NumSteps(binary);

        Assert.Equal(1001, numSteps);
    }
}